  pm.collectionVariables.get(key)
)
const requestHeaders = JSON.parse(
  JSON.stringify(request.headers).replace(/{{([A-Za-z]\w+)}}/g, (_, key) =>
    pm.collectionVariables.get(key)
  )
)

