        fixed
    }
  }
}

query GetAssets($after: String, $before: String, $first: Int, $last: Int) {
    assets(after: $after, before: $before, first:$first, last: $last) {
        edges {
            cursor
            node {
                code
                createdAt
                id
                scale
                withdrawalThreshold
                liquidityThreshold
                liquidity
                sendingFee {
                    id
                    type
                    basisPoints
                    fixed
                }
                receivingFee {
                    id
                    type
                    basisPoints
                    fixed
                }
            }
        }
        pageInfo {
            endCursor
            hasNextPage
            hasPreviousPage
            startCursor
        }
    }
}

